DECLARE @xml XML , @BIZ_ENTP_ID VARCHAR(MAX) = '' , @IN_VAL_ENTP VARCHAR(MAX) = '' DECLARE @TBL_ENTP AS TABLE(ENTP_ID VARCHAR(200)) SET @BIZ_ENTP_ID = 'C10 EA042,E71 AAL211,E71 AAH32,' -- 업체코드 -- SET @BIZ_ENTP_ID = SUBSTRING(@BIZ_ENTP_ID, 1, LEN(@BIZ_ENTP_ID) - 1) SET @IN_VAL_ENTP = REPLACE(@BIZ_ENTP_ID, ',' , '"/><R i="') SET @IN_VAL_ENTP = '<ROOT><R i="' + @IN_VAL_ENTP + '"/></ROOT>' SET @xml = @IN_VAL_ENTP
INSERT INTO @TBL_ENTP SELECT T.r.value('@i','varchar(20)') as ENTP_ID FROM @xml.nodes('/ROOT/R') T(r);
select * from @TBL_ENTP
